import $ from "jquery";

export function buttonToogle() {
  $("section").toggleClass("sidebar-toggled");
  $(".sidebar").toggleClass("toggled");
  $(window).resize(function() {
    if ($(window).width() < 768) {
      $("section").toggleClass("sidebar-toggled");
      $(".sidebar").toggleClass("toggled");
    }
  });
}
